--1. Đưa ra thông tin các sinh viên có tên là ThứcSELECT	*FROM	SinhVienWHERE	SinhVien.TenSV LIKE N'%Thức'--2. Đưa ra thông tin các sinh viên có họ là NguyễnSELECT	*FROM	SinhVienWHERE	SinhVien.TenSV LIKE N'Nguyễn%'--3. Đưa ra thông tin các sinh viên có chữ Thu trong họ và tênSELECT	*FROM	SinhVienWHERE	SinhVien.TenSV LIKE N'% Thu %'--4.Sửa thông tin họ tên thành “Nguyễn Hoàng Mai” với sinh viên có mã là AT1(AT170402)UPDATE SinhVienSET 	TenSV =N'Nguyễn Hoàng Mai'WHERE	MaSV ='AT170402'	--SELECT	*FROM	SinhVien--5.Sửa thông tin lớp thành L10 đối với sinh viên nam quê ở Hà NộiUPDATE SinhVienSET 	Lop ='L10'WHERE	Que=N'Hà Nội'	AND	GioiTinh='Nam'--6. Xóa thông tin sinh viên quê ở Hà Nội và Hà NamDELETE FROM SinhVienWHERE 	Que =N'Hà Nội'	OR	Que =N'Hà Nam'--7. Xóa thông tin của tất cả các sinh viên có trong bảng sinh viênDELETE FROM SinhVien--8. Đưa ra thông tin các sinh viên sinh tháng 9/2000SELECT	*FROM	SinhVienWHERE	SinhVien.NgaySinh LIKE '2000-09%'--9. Có bao nhiêu sinh viên nữ sinh trước tháng 12/2002SELECT	COUNT(*) [Số lượng sinh viên]FROM	SinhVienWHERE	MONTH(SinhVien.NgaySinh) < 12	AND	YEAR(SinhVien.NgaySinh) <= 2002	AND	GioiTinh =N'Nữ'